/* ==UserStyle==
@name npm Catppuccin
@namespace github.com/catppuccin/userstyles/styles/npm
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/npm
@version 0.0.5
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/npm/catppuccin.user.css
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Anpm
@description Soothing pastel theme for npm
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document domain("www.npmjs.com") {
  @media (prefers-color-scheme: light) {
    :root {
      #catppuccin(@lightFlavor, @accentColor);
    }
  }
  @media (prefers-color-scheme: dark) {
    :root {
      #catppuccin(@darkFlavor, @accentColor);
    }
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    color-scheme: if(@lookup = latte, light, dark);
    accent-color: @accent-color;

    ::selection {
      background-color: fade(@accent-color, 30%);
    }

    input,
    textarea {
      background-color: transparent;

      &:focus {
        box-shadow: 0 0 0 2px @accent-color;
      }

      &::placeholder {
        color: @subtext0 !important;
      }
    }

    @npmGradient: linear-gradient(139deg, @peach, @maroon, @red, @pink);

    --color-fg-default: @text;
    --color-fg-muted: @subtext1;
    --color-fg-subtle: @subtext0;
    --color-fg-on-emphasis: @base;
    --color-fg-brand: @red;
    --color-fg-accent: @blue;
    --color-fg-success: @green;
    --color-fg-attention: @yellow;
    --color-fg-danger: @red;
    --color-border-strong: @surface0;

    &,
    main {
      background-color: @base !important;
    }

    /* Primary background */
    .bg-white,
    ._4ea0e50d {
      background-color: @base;
    }

    /* Background layer background */
    ._7eb68a55,
    .e9998f88 {
      background-color: @mantle;
    }

    /* Primary text */
    ._99e3854f,
    .black,
    .black-90,
    .black-80,
    ._0b9b6c57,
    ._1b8c0a9f,
    p,
    a {
      color: @text;
    }

    /* Secondary text */
    .black-70,
    .black-60,
    .cd2827bb a {
      color: @subtext0;
    }
    ._66c2abad {
      color: @overlay0;
    }

    /* Inverse text */
    .white {
      color: @base;
    }

    /* Border colors */
    .b--black-10,
    .b--black-20 {
      border-color: @surface0;
    }

    /* --- ARBITRARY --- */

    /* Top loading bar */
    div.fixed.top-0.left-0.z-999 {
      background-color: @red;
    }

    /* Magnifying glass icon (search box, discover packages) */
    svg g[stroke="#777777"] {
      stroke: @subtext0;
    }

    /* "Beta" pill */
    .a17280e0 {
      color: @green;
      border-color: @green;
    }
    /* Green checkmark */
    [fill="#107010"] {
      fill: @green;
    }

    /* --- HEADER --- */
    [data-test-id="notification-banner"] {
      background-color: if(
        @lookup = latte,
        desaturate(lighten(@green, 30%), 5%),
        desaturate(darken(@green, 5%), 5%)
      );
      border-color: @green;

      #notification,
      button[aria-label="Close notification"] {
        color: if(@lookup = latte, @text, @base);
      }
    }
    header {
      border-bottom-color: @surface0;

      /* Top bar gradient */
      .e7070742 {
        border-image: @npmGradient 3;
      }

      /* Heart icon */
      ._0edb515f {
        color: @text;
      }

      /* Logo */
      a[href="/"] > svg > path {
        fill: @text;
      }

      /* Search box */
      form#search {
        button[type="submit"] {
          background-color: @subtext1;
        }

        > div {
          border-color: @crust;

          &:focus-within {
            border-color: @subtext1;
          }

          > div {
            background-color: @crust;

            input[type="search"] {
              color: @text;

              &:focus {
                box-shadow: none;
              }

              &::-webkit-search-cancel-button {
                display: none;
              }
            }
          }

          /* Floating live results */
          ul li {
            border-bottom-color: @surface0;
            color: @text;
          }
        }
      }

      /* Profile picture nvigation */
      #signup {
        border-color: @surface0;
      }
      nav > button[aria-label="Profile menu"] {
        /* Dropdown arrow */
        img[alt="avatar"] {
          border-color: @text;

          + div > svg {
            fill: @text;
          }
        }

        /* Popup */
        + span > div {
          border-color: @surface0;

          /* Popup triangle */
          &::after {
            border-bottom-color: @base;
          }
          &::before {
            border-bottom-color: @surface0;
          }

          /* Username */
          h2 {
            border-bottom-color: @surface1;
          }

          /* Navigation options */
          ul li a {
            &:hover,
            &:focus {
              background-color: @crust;
            }

            /* Red "Add Organization" button */
            &[href="/org/create"] {
              border-color: @surface0;
              color: @red;
              svg {
                fill: @red;
              }
            }
          }
        }
      }
    }

    /* --- HOME PAGE --- */

    article > section:nth-of-type(1) {
      background-image: linear-gradient(
          270deg,
          fade(@red, 16%) 0%,
          fade(@red, 56%) 18.45%,
          fade(@red, 80%) 49.67%,
          fade(@red, 56%) 82.52%,
          fade(@red, 19%) 99.7%,
          fade(@red, 0%) 99.71%,
          fade(@red, 0%) 99.72%,
          fade(@red, 16%) 99.73%
        ),
        url("https://static-production.npmjs.com/abf53a31b2da4657a1a004ee9358551c.png");

      a[href="/signup"] {
        background-color: @yellow;
      }
      a[href="/products/pro"] {
        background-color: @red;
        border-color: @text;
      }
    }
    article > section:nth-of-type(2) {
      color: @text;
