  ::selection {
    background-color: fade(@accent-color, 30%);
  }

  input,
  textarea {
    &::placeholder {
      color: @subtext0 !important;
    }
  }

  --text-primary: @text;
  --text-secondary: @subtext0;
  --text-active: @overlay1;
  --text-inactive: fadeout(@subtext0, 40%);
